mapper.select:
select d.* , c.name as categoryName from dish d left join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

select d.* ,df.id fid,df.name fname ,df.value fvalue
from dish d left join dish_flavor df on d.id = df.dish_id
where d.id=#{id}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}
